mapper.select:
SELECT a.id,sum(a.count) count,MIN(a.start_time) startTime,MAX(a.end_time) endTime, b.prodName ,c.name memberName,sum(a.count*b.price) price,b.batch,b.come_count comeCount FROM tb_member_prod a, ( SELECT come.*, prod.name prodName FROM tb_prod_come come JOIN tb_prod prod ON come.prod_id = prod.id
where come.prod_id=#{prodId,jdbcType=INTEGER}
) b,tb_member c WHERE a.come_id=b.id and a.member_id=c.id
a.start_time>=#{startTime,jdbcType=VARCHAR}
a.end_time>=#{endTime,jdbcType=VARCHAR}
GROUP BY c.`name`,b.prodName,b.batch ORDER BY count desc;

mapper.insert:
insert into tb_member_prod (id, member_id, come_id, 
  count, start_time, end_time, 
  create_time, create_user, update_time
  )
values (#{id,jdbcType=INTEGER}, #{memberId,jdbcType=INTEGER}, #{comeId,jdbcType=INTEGER}, 
  #{count,jdbcType=INTEGER}, #{startTime,jdbcType=VARCHAR}, #{endTime,jdbcType=VARCHAR},
  #{createTime,jdbcType=TIMESTAMP}, #{createUser,jdbcType=INTEGER}, #{updateTime,jdbcType=TIMESTAMP}
  )
